#                                           ONE TIME FUNCTION TO CHECK TOTAL AVAILABLE DATA
####################################################################################################################################
def limiChecker(term,location,categories):
    params = {'term':term,'limit':1,'location':location,'offset':offset,'locale':locale,'categories':categories}
    req=requests.get(url, params=params, headers=head)
    if(req.status_code == 429):
        #print("Daily Limit Reached !!!")
        return "No"
    #print('The status code is {}'.format(req.status_code))
    parsed = json.loads(req.text)
    total = parsed["total"]
    return total


