#                                                           MAIN FUNCTION
####################################################################################################################################
def yelpBusinessSearch(term,location,categories,file_name):
    params = {'term':term,'limit':50,'location':location,'offset':offset,'locale':locale,'categories':categories}
    req=requests.get(url, params=params, headers=head)
    if(req.status_code == 429):
        return "No"
    #print('The status code is {}'.format(req.status_code))
    

    parsed = json.loads(req.text)

    businesses = parsed["businesses"]

    for business in businesses:
        name = business["name"]
        phone = business["phone"]
        rating = business["rating"]
        review = business["review_count"]
        address = " ".join(business["location"]["display_address"])
        yelpId = business["id"]
        yelpURL = business["url"]
        try:
            websiteURL = yelpURL[0:yelpURL.find("?")]
        except:
            websiteURL = yelpURL
        
        print("Name   : ",name)
        print("Phone  : ",phone)
        print("Rating : ",rating )
        print("Reviews: ",review)
        print("Address: ",address)
        print("yelp id: ",yelpId)
        print("yelp URL: ",websiteURL)
        print("\n")
        record.append((name,phone,rating,review,address,yelpId,websiteURL))
        df=pd.DataFrame(record,columns=['Name','Phone','Rating','Review','Address','YelpId','YelpURL'])
        df.to_csv(file_name + '.csv',index=False,encoding='utf-8')
 
